import React, { useState } from 'react';
import Expenses from './components/Expenses/Expenses';
import NewExpense from './components/NewExpense/NewExpense';

const App = () => {
  const dummyExpenses = [
    {
      id: 'e1',
      expenseDate: new Date(2021, 11, 7),
      expenseTitle: 'Car Insurance',
      expenseAmount: 294.8
    },
    {
      id: 'e2',
      expenseDate: new Date(2021, 11, 8),
      expenseTitle: 'Bike Insurance',
      expenseAmount: 294.8
    },
    {
      id: 'e3',
      expenseDate: new Date(2021, 11, 9),
      expenseTitle: 'Tractor Insurance',
      expenseAmount: 294.8
    },
    {
      id: 'e4',
      expenseDate: new Date(2021, 11, 10),
      expenseTitle: 'Cycle Insurance',
      expenseAmount: 294.8
    },
  ]

  //currently expenses in below statement is : dummyExpenses
  const [expenses, setExpenses] = useState(dummyExpenses);

  const addExpenseHandler = (expense) => {
    // console.log("In app.js!!");
    // console.log(expense);
    setExpenses( (prevExpenses) => {
       return [expense, ...prevExpenses];
    });
  }

  return (
    <div>
      <h2>Let's get started!</h2>
      <NewExpense onAddExpense={addExpenseHandler}/>
      <Expenses items={expenses}/>
    </div>
  );
}

export default App;
