import React, { useState } from 'react';
import './ExpenseForm.css';

const ExpenseForm = () => {

   const [enteredTitle, setEntiredTitle] = useState('');

   const titleChangeHandler = (event) => {
      setEntiredTitle(event.target.value);
   }

   return (
       <div>
         <div className="new-expense__controls">
           <div className="new-expense__control">
              <label className="new-expense__control label">Title</label>
              <input className="new-expense__control input" onChange={titleChangeHandler} type='text'/>
           </div>

           <div className="new-expense__control">
              <label className="new-expense__control label">Amount</label>
              <input className="new-expense__control input" type='text'/>
           </div>

           <div className="new-expense__control">
              <label className="new-expense__control label">Date</label>
              <input className="new-expense__control input" type='date' min="2020-01-01" step="2022-01-01"/>
           </div>
         </div>

        <div> 
         <button type='submit' className="new-expense__actions">Add Expense</button>
        </div>

       </div>
   );
}

export default ExpenseForm;
